import React, { useState } from 'react'

const Config = (props) => {
    // ミドルスペック
    const middleProbabilityOfLeft = process.env.REACT_APP_MIDDLE_PROBABILITY_OF_LEFT
    const middleProbabilityOfRight = process.env.REACT_APP_MIDDLE_PROBABILITY_OF_RIGHT
    const middleWeightOfChanceTime = process.env.REACT_APP_MIDDLE_WEIGHT_OF_CHANCE_TIME
    const middleWeightOfNotChanceTime = process.env.REACT_APP_MIDDLE_WEIGHT_OF_NOT_CHANCE_TIME
    const middleLeftBonus = process.env.REACT_APP_MIDDLE_LEFT_BONUS
    const middleSuperBigBonus = process.env.REACT_APP_MIDDLE_SUPER_BIG_BONUS
    const middleBigBonus = process.env.REACT_APP_MIDDLE_BIG_BONUS
    const middleRegularBonus = process.env.REACT_APP_MIDDLE_REGULAR_BONUS
    const middleWeightOfSBB = process.env.REACT_APP_MIDDLE_REGULAR_BONUS
    const middleWeightOfBB = process.env.REACT_APP_MIDDLE_WEIGHT_OF_BB
    const middleWeightOfRB = process.env.REACT_APP_MIDDLE_WEIGHT_OF_RB

    const [isMiddle, setIsMiddle] = useState(false)
    const [isLightMiddle, setIsLightMiddle] = useState(false)
    const [isAma, setIsAma] = useState(false)

    const handleOpenMiddle = () => {
        setIsMiddle(true)
        setIsLightMiddle(false)
        setIsAma(false)
    }
    const handleOpenLightMiddle = () => {
        setIsMiddle(false)
        setIsLightMiddle(true)
        setIsAma(false)
    }
    const handleOpenAma = () => {
        setIsMiddle(false)
        setIsLightMiddle(false)
        setIsAma(true)
    }

    const handleStartMiddle = () => {

        props.setModalOpen(true)
    }
    const handleStartLightMiddle = () => {
        props.setModalOpen(true)
    }
    const handleStartAma = () => {
        props.setModalOpen(true)
    }

    return (
        <div className='modal-wrapper'>
            <div className='modalContents'>
                <h2>SPEC SELECT</h2>
                <div className='specSelect'>
                    <button className='selectMiddle' onClick={handleOpenMiddle}>middle</button>
                    <button className='selectLightMiddle' onClick={handleOpenLightMiddle}>lightMiddle</button>
                    <button className='selectAma' onClick={handleOpenAma}>ama</button>
                    {isMiddle ? <p className='aboutMiddle'>aboutMiddle</p> : ''}
                    {isLightMiddle ? <p className='aboutLightMiddle'>aboutLightMiddle</p> : ''}
                    {isAma ? <p className='aboutAma'>aboutAma</p> : ''}
                    {isMiddle ? <button className='startMiddle' onClick={handleStartMiddle}>start</button> : ''}
                    {isLightMiddle ? <button className='startLightMiddle' onClick={handleStartLightMiddle}>start</button> : ''}
                    {isAma ? <button className='startAma' onClick={handleStartAma}>start</button> : ''}
                </div>
            </div>
        </div>
    )
}

export default Config
