import React, { useState } from 'react'

const Display = (props) => {

    const kakuhenLottery = () => {

    }

    const normalLottery = () => {
        if (props.credits >= 15) {
            props.setCredit(props.credits - 15)
            const number = Math.floor(Math.random * 100)
            console.log(number);
        } else {
            window.alert('玉がありません')
        }

    }

    return (
        <div>
            {props.isBonusTime
                ?
                <div>
                    <p>確変</p>
                    <button onClick={kakuhenLottery}>抽選する</button>
                </div>
                :
                <div>
                    <p>通常</p>
                    <button onClick={normalLottery}>抽選する</button>
                </div>
            }
        </div>
    )
}

export default Display
