import './App.css';
import React, { useState } from 'react';
import { InvestmentButton, Data, Display } from './components';

function App() {
  const [totalInvestments, setTotalInvestment] = useState(Number(0));//総投資
  const [investments, setInvestment] = useState(Number(0));// 投資金額（パッキーカード）
  const [credits, setCredit] = useState(Number(0));// クレジット（上皿）
  const [mochidama, setMochidama] = useState(Number(1000));// 持ち球
  const [bonusTimes, setBonusTime] = useState(Number(0));// 大当たり回数
  const [isChanceTime, setIsChanceTime] = useState(false);// 確率変動
  const [rotate, setRotate] = useState(Number(0));

  const handleAdd = (price) => {
    setInvestment(Number(investments) + Number(price));
    // console.log(investments);
  }

  return (
    <div className="">
      <InvestmentButton 
      // onAdd={handleAdd}
      investments={investments}
      setInvestment={setInvestment}
      credits={credits}
      setCredit={setCredit}
      mochidama={mochidama}
      setMochidama={setMochidama}
      />
      <Display />
      <Data
        totalInvestments={totalInvestments}
        investments={investments}
        credits={credits}
        mochidama={mochidama}
        bonusTimes={bonusTimes}
        rotate={rotate}
      />
    </div>
  );
}

export default App;
