import React, { useState, useRef, useEffect } from 'react'

const Display = (props) => {
    const normalHit = 1;//通常時大当たり
    const kakuhenHit = 1;

    //.envから呼び出し
    const probabilityOfLeft = Number(process.env.REACT_APP_PROBABILITY_OF_LEFT)
    const probabilityOfRight = Number(process.env.REACT_APP_PROBABILITY_OF_RIGHT)
    const weightOfChanceTime = Number(process.env.REACT_APP_WEIGHT_OF_CHANCE_TIME)
    const wightOfNotChanceTime = Number(process.env.REACT_APP_WEIGHT_OF_NOT_CHANCE_TIME)
    const leftBonus = Number(process.env.REACT_APP_LEFT_BONUS)
    const superBigBonus = Number(process.env.REACT_APP_SUPER_BIG_BONUS)
    const bigBonus = Number(process.env.REACT_APP_BIG_BONUS)
    const regularBonus = Number(process.env.REACT_APP_REGULAR_BONUS)
    const weightOfSBB = Number(process.env.REACT_APP_WEIGHT_OF_SBB)
    const weightOfBB = Number(process.env.REACT_APP_WEIGHT_OF_BB)
    const speedOfShoot = Number(process.env.REACT_APP_SPEED_OF_SHOOT)
    // const weightOfRB = process.env.REACT_APP_WEIGHT_OF_RB

    const creditsRef = useRef(props.credits)
    creditsRef.current = props.credits
    const rotateRef = useRef(props.rotate)
    rotateRef.current = props.rotate
    const totalRotateRef = useRef(props.totalRotate)
    totalRotateRef.current = props.totalRotate

    const isKakuhen = () => {
        const data = { '通常': wightOfNotChanceTime, '確変': weightOfChanceTime }
        const rand = Math.floor(Math.random() * 100)
        let rate = 0
        for (const prop in data) {
            rate += data[prop]
            if (rand <= rate) {
                window.alert('通常')
                break
            } else {
                window.alert('確変')
                props.setIsChanceTime(true)
                break
            }
        }
    }

    const kakuhenLottery = () => {
        props.setRotate(rotateRef.current + 1)
        props.setTotalRotate(totalRotateRef.current + 1)
        const number = Math.floor(Math.random() * probabilityOfRight)
        if (kakuhenHit === number) {
            window.alert('BONUS')
            clearInterval()
            setFlg(false)
            bonusLottery()
        } else {
            console.log('NEXT')
        }
    }

    const bonusLottery = () => {
        const data = {
            'SUPER BIG BONUS': weightOfSBB,//SBの確率
            'BIG BONUS': weightOfBB,//BBの確率
            //100 - SB -BB = RBの確率
        }
        const rand = Math.floor(Math.random() * 100)
        let result = '';
        let rate = 0
        for (const prop in data) {
            rate += data[prop]
            if (rand <= rate) {
                result = prop;
                break
            } else {
                result = 'BONUS'
            }
        }

        props.setBonusTime(props.bonusTimes + 1);

        switch (result) {
            case 'SUPER BIG BONUS':
                window.alert('SUPER BIG BONUS')
                props.setMochidama(props.mochidama + superBigBonus)
                break;
            case 'BIG BONUS':
                window.alert('BIG BONUS')
                props.setMochidama(props.mochidama + bigBonus)
                break;
            case 'BONUS':
                props.setMochidama(props.mochidama + regularBonus)
                props.setIsChanceTime(false)
                window.alert('左打ちに戻してくれ')
                break;
            default:
                window.alert('係員を呼んでください')
        }
        props.setRotate(0)
    }

    // n回転/1kの処理
    const normalHandle = () => {
        if (creditsRef.current <= 0) {
            window.alert('玉がありません')
            clearInterval()
            setFlg(false)
            return
        } else {
            props.setCredit(creditsRef.current - 1)
            const number = Math.floor(Math.random() * 10)
            if (number === normalHit) {
                normalLottery()
            }
        }
    }

    // 通常時の抽選
    const normalLottery = () => {
        const number = Math.floor(Math.random() * probabilityOfLeft)
        if (number === normalHit) {
            clearInterval()
            setFlg(false)
            window.alert('大当たり!')
            props.setMochidama(props.mochidama + leftBonus)
            props.setBonusTime(props.bonusTimes + 1)
            isKakuhen();
            props.setRotate(0)
        } else {
            console.log('next')
            props.setRotate(rotateRef.current + 1)
        }
        props.setTotalRotate(totalRotateRef.current + 1)
    }

    // const [count, setCount] = useState(0)
    // const intervalRef = useRef(null)

    // const start = useCallback(() => {
    //     if (intervalRef.current !== null) {
    //         return;
    //     }
    //     intervalRef.current = setInterval(() => {
    //         normalHandle()
    //     }, 500)
    // }, [])

    // const stop = useCallback(() => {
    //     if (intervalRef.current === null) {
    //         return
    //     }
    //     clearInterval(intervalRef.current)
    //     intervalRef.current = null
    // }, [])

    //20230411
    const [flg, setFlg] = useState(false)
    let interval = null

    useEffect(() => {
        if (flg) {
            console.log('開始')
            interval = setInterval(() => {
                if (!props.isChanceTime) {
                    normalHandle()
                } else {
                    kakuhenLottery()
                }
            }, speedOfShoot)
            return () => {
                console.log('停止')
                clearInterval(interval)
            }
        }
    }, [flg])


    return (
        <>
            <button onClick={() => setFlg(!flg)} className='handle'>handle</button>
            <div className='display'>

                {props.isChanceTime
                    ?
                    <p className='test'>確変</p>
                    :

                    <p className='test'>通常</p>

                }
            </div>
        </>
    )
}

export default Display
