import React, { useState } from 'react'

const Header = () => {
    const [isOpenConfig, setIsOpenConfig] = useState(false)
    const handleOpenConfig = () => {
        setIsOpenConfig(!isOpenConfig)
    }
    console.log(isOpenConfig)
    return (
        <>
            <div className='header'>
                <p className='logo'>Pachinko Simulator</p>
                <p className='config' onClick={handleOpenConfig}>config</p>
            </div>
            <div className={isOpenConfig ? 'headerConfig' : 'is-open headerConfig'}>
                <p>speed control</p>
                <p>n回転/1</p>
            </div>
        </>
    )
}

export default Header
