import React, { useState, useRef, useCallback, useEffect } from 'react'

const Display = (props) => {
    const normalHit = 1;//通常時大当たり
    const kakuhenHit = 1;
    // const [stock, setStock] = useState(Number(0))
    const creditsRef = useRef(props.credits)
    creditsRef.current = props.credits
    const rotateRef = useRef(props.rotate)
    rotateRef.current = props.rotate
    const totalRotateRef = useRef(props.totalRotate)
    totalRotateRef.current = props.totalRotate

    const isKakuhen = () => {
        const data = { '通常': 40, '確変': 60 }
        const rand = Math.floor(Math.random() * 100)
        let rate = 0
        for (const prop in data) {
            rate += data[prop]
            if (rand <= rate) {
                window.alert('通常')
                break
            } else {
                window.alert('確変')
                props.setIsChanceTime(true)
                break
            }
        }
    }

    const kakuhenLottery = () => {
        props.setRotate(props.rotate + 1)
        props.setTotalRotate(props.totalRotate + 1)
        const number = Math.floor(Math.random() * 75)
        if (kakuhenHit === number) {
            window.alert('BONUS')
            bonusLottery()
        } else {
            console.log('NEXT')
        }
    }

    const bonusLottery = () => {
        const data = {
            'SUPER BIG BONUS': 20,//20%
            'BIG BONUS': 60//60%
            //BONUS20%
        }
        const rand = Math.floor(Math.random() * 100)
        let result = '';
        let rate = 0
        for (const prop in data) {
            rate += data[prop]
            if (rand <= rate) {
                result = prop;
                break
            } else {
                result = 'BONUS'
            }
        }

        props.setBonusTime(props.bonusTimes + 1);

        console.log(result)
        switch (result) {
            case 'SUPER BIG BONUS':
                props.setMochidama(props.mochidama + 1500)
                break;
            case 'BIG BONUS':
                props.setMochidama(props.mochidama + 1000)
                break;
            case 'BONUS':
                props.setMochidama(props.mochidama + 500)
                props.setIsChanceTime(false);
                break;
            default:
                console.log('係員を呼んでください')
        }
        props.setRotate(0)
    }

    // n回転/1kの処理
    const normalHandle = () => {
        if (creditsRef.current <= 0) {
            window.alert('玉がありません')
            clearInterval()
            setFlg(false)
            return
        } else {
            props.setCredit(creditsRef.current - 1)
            const number = Math.floor(Math.random() * 10)
            if (number === normalHit) {
                normalLottery()
            }
        }
    }

    // 通常時の抽選
    const normalLottery = () => {
        const number = Math.floor(Math.random() * 10)
        if (number === normalHit) {
            clearInterval()
            setFlg(false)
            window.alert('大当たり！')
            props.setMochidama(props.mochidama + 800)
            props.setBonusTime(props.bonusTimes + 1)
            isKakuhen();
            props.setRotate(0)
        } else {
            console.log('next')
            props.setRotate(rotateRef.current + 1)
        }
        props.setTotalRotate(totalRotateRef.current + 1)
    }

    // const [count, setCount] = useState(0)
    // const intervalRef = useRef(null)

    // const start = useCallback(() => {
    //     if (intervalRef.current !== null) {
    //         return;
    //     }
    //     intervalRef.current = setInterval(() => {
    //         normalHandle()
    //     }, 500)
    // }, [])

    // const stop = useCallback(() => {
    //     if (intervalRef.current === null) {
    //         return
    //     }
    //     clearInterval(intervalRef.current)
    //     intervalRef.current = null
    // }, [])

    //20230411
    const [flg, setFlg] = useState(false)
    let interval = null

    const hoge = 1

    if (hoge === 1) {
        useEffect(() => {
            if (flg) {
                console.log('開始')
                interval = setInterval(() => {
                    normalHandle()
                }, 300)
                return () => {
                    console.log('停止')
                    clearInterval(interval)
                }
            }
        }, [flg])
    }



    return (
        <div>
            <button onClick={() => setFlg(!flg)}>handle</button>

            {props.isChanceTime
                ?
                <div>
                    <p>確変</p>
                    <button onClick={kakuhenLottery}>抽選する</button>
                </div>
                :
                <div>
                    <p>通常</p>
                    <button onClick={normalHandle}>抽選する</button>
                </div>
            }
        </div>
    )
}

export default Display
