import './App.css';
import React, { useState } from 'react';
import { Button, Data, Display, SideData } from './components';

function App() {
  const [totalInvestments, setTotalInvestment] = useState(Number(0));//総投資
  const [investments, setInvestment] = useState(Number(0));// 投資金額（パッキーカード）
  const [credits, setCredit] = useState(Number(0));// クレジット（上皿）
  const [mochidama, setMochidama] = useState(Number(0));// 持ち球
  const [bonusTimes, setBonusTime] = useState(Number(0));// 大当たり回数
  const [isChanceTime, setIsChanceTime] = useState(false);// 確率変動
  const [rotate, setRotate] = useState(Number(0));//回転数
  const [totalRotate, setTotalRotate] = useState(Number(0));//総回転数
console.log(process.env.REACT_APP_HELLO_WORLD)
  return (
    <div className="">
      <p>{process.env.REACT_APP_HELLO_WORLD}</p>
      <h1>CR FEVER機動戦士リアクト</h1>
      <div className='container'>
        <Button className='hoge'
          investments={investments}
          setInvestment={setInvestment}
          credits={credits}
          setCredit={setCredit}
          mochidama={mochidama}
          setMochidama={setMochidama}
          totalInvestments={totalInvestments}
          setTotalInvestment={setTotalInvestment}
        />
        <Data
          // totalInvestments={totalInvestments}
          // investments={investments}
          // credits={credits}
          // mochidama={mochidama}
          bonusTimes={bonusTimes}
          rotate={rotate}
          totalRotate={totalRotate}
        />
        <SideData
          investments={investments}
          totalInvestments={totalInvestments}
          mochidama={mochidama}
        />
        <Display
          credits={credits}
          setCredit={setCredit}
          bonusTimes={bonusTimes}
          setBonusTime={setBonusTime}
          isChanceTime={isChanceTime}
          setIsChanceTime={setIsChanceTime}
          rotate={rotate}
          setRotate={setRotate}
          totalRotate={totalRotate}
          setTotalRotate={setTotalRotate}
          mochidama={mochidama}
          setMochidama={setMochidama}
        />
      </div>
    </div>
  );
}

export default App;
