import React, { useState } from 'react'

const InvestmentButton = ({ props }) => {
  const [price, setPrice] = useState(1000);
  const handleChange = (e) => {
    setPrice(e.target.value);
  }

  const handleInvestment = (e) => {
    // onAdd(price);
    // setPrice(0);
    props.setInvestment(price)
  }


  return (
    <div>
      <select className="" id="" value={price} onChange={handleChange}>
        <option value="1000">1000</option>
        <option value="5000">5000</option>
        <option value="10000">10000</option>
      </select>
      <button onClick={handleInvestment}>投資</button>
    </div>
  )
}

export default InvestmentButton
