import React, { useState, useEffect } from 'react'

const Result = (props) => {
    const [results, setResults] = useState(Number(0))
    const showResult = () => {
        if (props.finish) {
            props.setFlg(false)
            setResults((props.mochidama + props.credits) * 4 - props.totalInvestments)
            console.log(results)
        }
    }
    useEffect(() => {
        showResult()
    }, [props.finish])


    const handleContinue = () => {
        // stateはそのまま
        props.setFinish(false)
    }

    const handleReset = () => {
        // 設定以外のstateをリセット
        props.setCredit(0)
        props.setBonusTime(0)
        props.setIsChanceTime(0)
        props.setStart(0)
        props.setTotalStart(0)
        props.setMochidama(0)
        props.setTotalInvestment(false)
        props.setFinish(false)
    }

    return (
        <div className={props.finish ? 'modal-wrapper' : 'none'}>
            <div className='modalContents'>
                <p>result</p>
                <p>総投資{props.totalInvestments}</p>
                <p>持ち球{props.mochidama + props.credits}</p>
                <p>
                    結果
                {results >= 0 ? '+' + results : results}
                円
                </p>
                <p>
                    <button onClick={handleContinue}>Continue</button>
                    <button onClick={handleReset}>reset</button>
                </p>
            </div>
        </div>
    )
}

export default Result
