var tid = setInterval(function(){
  hoge();
  if(i > 3){
    clearInterval(tid);
  }
}, 1000);

    return (
        <div>
            <button onClick={handleChange}>ハンドル</button>
            {props.isChanceTime
                ?
                <div>
                    <p>確変</p>
                    <button onClick={kakuhenLottery}>抽選する</button>
                </div>
                :
                <div>
                    <p>通常</p>
                    <button onClick={normalLottery}>抽選する</button>
                </div>
            }
        </div>
    )
}

