import React, { useState } from 'react'

const InvestmentButton = (props) => {
  const [price, setPrice] = useState(1000);
  const handleChange = (e) => {
    setPrice(e.target.value);
  }

  const handleInvestment = (e) => {
    // onAdd(price);
    // setPrice(0);
    props.setInvestment(Number(props.investments) + Number(price));
  }

  const handleTamakashi = (e) => {
    if (props.mochidama >= 125) {
      props.setCredit(props.credits + 10);
      props.setMochidama(props.mochidama - 125)
    } else if (props.investments >= 500) {
      props.setCredit(props.credits + 10);
      props.setInvestment(props.investments - 500)
    }
  }

  return (
    <div>
      <select className="" id="" value={price} onChange={handleChange}>
        <option value="1000">1000</option>
        <option value="5000">5000</option>
        <option value="10000">10000</option>
      </select>
      <button onClick={handleInvestment}>投資</button>
      <button onClick={handleTamakashi}>玉貸</button>
    </div>
  )
}

export default InvestmentButton
